  render() {
    const {array} = this.state;

    return (
      <div className="array-container">
        {/* {array.map((value, index) => ( ... ))}: This uses the map function to iterate over each value in the array state. It creates a <div> element for each value and sets its height based on the value, creating the bar visualization effect. */}
