export function getQuickSortAnimations(array) {
    const animations = [];
    if (array.length <= 1) return array;
    quickSort(array, 0, array.length - 1, auxiliaryArray, animations);
    return animations;
  }

  function quickSort(mainArray, startIdx, endIdx, auxiliaryArray, animations){
    if (startIdx >= endIdx) return;  

    var pivot_pos = quick_partition(mainArray, startIdx, endIdx, animations);
    quickSort(mainArray, startIdx, pivot_pos - 1, mainArray, animations);
    quickSort(mainArray, pivot_pos + 1, endIdx, mainArray, animations);
  }

  function quick_partition(mainArray, startIdx, endIdx, animations){
    var i = startIdx + 1;
    var piv = mainArray[startIdx];
    let animationStep = [startIdx, startIdx, 1];
    animations.push(animationStep);
    for(var j = startIdx + 1; j <= endIdx; j++){
        if(piv > mainArray[j]){
            let animationStep = [j, j, 1];
            animations.push(animationStep);

            animationStep = [i, i, 2];
            animations.push(animationStep);
            animationStep = [j, j, 2];
            animations.push(animationStep);

            var temp = mainArray[i];
            mainArray[i] = mainArray[j];
            mainArray[j] = temp;

            animationStep = [i, i, 2];
            animations.push(animationStep);
            animationStep = [j, j, 2];
            animations.push(animationStep);
            animationStep = [i, i, 4];
            animations.push(animationStep);
            animationStep = [j, j, 4];
            animations.push(animationStep);
            animationStep = [i, i, 5];
            animations.push(animationStep);
            animationStep = [j, j, 5];
            animations.push(animationStep);

            i++;
        }
    }
    animationStep = [startIdx, startIdx, 2];
    animations.push(animationStep);
    animationStep = [i - 1, i - 1, 2];
    animations.push(animationStep);

    var temp = mainArray[i - 1];
    mainArray[i - 1] = mainArray[startIdx];
    mainArray[startIdx] = temp;

    animationStep = [startIdx, startIdx, 2];
    animations.push(animationStep);
    animationStep = [i - 1, i - 1, 2];
    animations.push(animationStep);
    animationStep = [startIdx, startIdx, 4];
    animations.push(animationStep);
    animationStep = [i - 1, i - 1, 4];
    animations.push(animationStep);

    for(var t = startIdx; t <= i; t++){
        animationStep = [t, t, 3];
        animations.push(animationStep);
    }

    return i - 1;
    }
