export function getBubbSortAnimations(array) {
    const animations = [];
    if (array.length <= 1) return array;
    quickSort(array, 0, array.length - 1, animations);
    return animations;
}

function quickSort(mainArray, startIdx, endIdx, animations) {
    if (startIdx >= endIdx) return;

    var pivot_pos = quick_partition(mainArray, startIdx, endIdx, animations);
    quickSort(mainArray, startIdx, pivot_pos - 1, animations);
    quickSort(mainArray, pivot_pos + 1, endIdx, animations);
}

function quick_partition(mainArray, startIdx, endIdx, animations) {
    var i = startIdx + 1;
    var piv = mainArray[startIdx];
    let animationStep = [startIdx, startIdx, 1];
    animations.push(animationStep);
    for (var j = startIdx + 1; j <= endIdx; j++) {
        if (piv > mainArray[j]) {
            let animationStep = [j, j, 1];
            animations.push(animationStep); // color change

            animationStep = [i, i, 2];
            animations.push(animationStep); // color change
            animationStep = [j, j, 2];
            animations.push(animationStep); // color change

            var temp = mainArray[i];
            mainArray[i] = mainArray[j];
            mainArray[j] = temp;

            animationStep = [i, i, 2];
            animations.push(animationStep); // color change
            animationStep = [j, j, 2];
            animations.push(animationStep); // color change
            animationStep = [i, mainArray[i], 4]; // height change
            animations.push(animationStep);
            animationStep = [j, mainArray[j], 4]; // height change
            animations.push(animationStep);
            animationStep = [i, i, 5];
            animations.push(animationStep); // color change
            animationStep = [j, j, 5];
            animations.push(animationStep); // color change

            i++;
        }
    }
    animationStep = [startIdx, startIdx, 2];
    animations.push(animationStep); // color change
    animationStep = [i - 1, i - 1, 2];
    animations.push(animationStep); // color change

    var temp = mainArray[i - 1];
    mainArray[i - 1] = mainArray[startIdx];
    mainArray[startIdx] = temp;

    animationStep = [startIdx, startIdx, 2]; // color change
    animations.push(animationStep);
    animationStep = [i - 1, i - 1, 2]; // color change
    animations.push(animationStep);
    animationStep = [startIdx, mainArray[startIdx], 4]; // height change
    animations.push(animationStep);
    animationStep = [i - 1, mainArray[i - 1], 4]; // height change
    animations.push(animationStep);

    for (var t = startIdx; t <= i; t++) {
        animationStep = [t, t, 3]; // color change
        animations.push(animationStep);
    }

    return i - 1;
}
