export function getMergeSortAnimations(array) {
  const animations = [];
  if (array.length <= 1) return array;
  const auxiliaryArray = array.slice();
  mergeSortHelper(array, 0, array.length - 1, auxiliaryArray, animations);
  return animations;
}

function mergeSortHelper(mainArray, startIdx, endIdx, auxiliaryArray, animations){
  if (startIdx === endIdx) return;  
  const middleIdx = Math.floor((startIdx + endIdx) / 2);
  const animationStep = [middleIdx, middleIdx, 1];
  animations.push(animationStep); // IMP YELLOW
  // we will use aulixary in place of main arry because we are overwriting changes in main array
  // so go according to main array
  mergeSortHelper(auxiliaryArray, startIdx, middleIdx, mainArray, animations);
  mergeSortHelper(auxiliaryArray, middleIdx + 1, endIdx, mainArray, animations);
  // This below 2 lines will give wrong answer reason explained above
  // mergeSortHelper(mainArray, startIdx, middleIdx, auxiliaryArray, animations);
  // mergeSortHelper(mainArray, middleIdx + 1, endIdx, auxiliaryArray, animations);
  doMerge(mainArray, startIdx, middleIdx, endIdx, auxiliaryArray, animations);
}

function doMerge(mainArray, startIdx, middleIdx, endIdx, auxiliaryArray, animations){
  let k = startIdx;
  let i = startIdx;
  let j = middleIdx + 1;
  while (i <= middleIdx && j <= endIdx) {
    const animationStep = [i, j, 2];
    animations.push(animationStep); // push them once to change their color. // IMP RED
    animationStep = [i, j, 3];
    animations.push(animationStep); // push them a second time to revert their color. // IMP GREEN
    if (auxiliaryArray[i] <= auxiliaryArray[j]) {
      // We overwrite the value at index k in the original array with the
      // value at index i in the auxiliary array.
      animationStep = [k, auxiliaryArray[i], 4];
      animations.push(animationStep); // push a third time to change the height
      mainArray[k++] = auxiliaryArray[i++];
    } else {
      animationStep = [k, auxiliaryArray[j], 4];
      animations.push(animationStep);
      mainArray[k++] = auxiliaryArray[j++];
    }
  }
  while (i <= middleIdx) {
    const animationStep = [i, i, 2];
    animations.push(animationStep); // push them once to change their color. // IMP RED
    animationStep = [i, i, 3];
    animations.push(animationStep); // push them a second time to revert their color. // IMP GREEN
    animations.push([k, auxiliaryArray[i]]);
    mainArray[k++] = auxiliaryArray[i++];
  }
  while (j <= endIdx) {
    const animationStep = [j, j, 2];
    animations.push(animationStep); // push them once to change their color. // IMP RED
    animationStep = [j, j, 3];
    animations.push(animationStep); // push them a second time to revert their color. // IMP GREEN
    animations.push([k, auxiliaryArray[j]]);
    mainArray[k++] = auxiliaryArray[j++];
  }
}
