export function getBubbleSortAnimations(array) {
    const animations = [];
    if (array.length <= 1) return array;
    bubbleSort(array, 0, array.length - 1, animations);
    return animations;
}

function bubbleSort(mainArray, startIdx, endIdx, animations) {
    for (let i = startIdx; i <= endIdx; i++){
        let j;
        for(j = 0; j <= endIdx - i - 1; j++){
            let animationStep = [j, j, 1];
            animations.push(animationStep);
            if(mainArray[j] > mainArray[j + 1]){
                animationStep = [j, j, 2];
                animations.push(animationStep);
                animationStep = [j + 1, j + 1, 2];
                animations.push(animationStep);

                var temp = mainArray[j];
                mainArray[j] = mainArray[j + 1];
                mainArray[j + 1] = temp;

                animationStep = [j, j, 2];
                animations.push(animationStep);
                animationStep = [j + 1, j + 1, 2];
                animations.push(animationStep);
                animationStep = [j, mainArray[j], 4];
                animations.push(animationStep);
                animationStep = [j + 1, mainArray[j + 1], 4];
                animations.push(animationStep);
            }
            animationStep = [j, j, 5];
            animations.push(animationStep);
        }
        animationStep = [j, j, 3];
        animations.push(animationStep);
    }
    animationStep = [j, 0, 3];
        animations.push(animationStep);
}
