export function getBubbleSortAnimations(array) {
    const animations = [];
    if (array.length <= 1) return array;
    bubbleSort(array, 0, array.length - 1, animations);
    for(var i = 0; i < array.length; i++){
        let animationStep = [i, i, 5];
        animations.push(animationStep);
    }
    return animations;
}

